drop table if exists t;

create table t ( 
    int_col int, 
    float_col float, 
    string_col varchar(100), 
    time_col time, 
    datetime_col datetime
);

create index i_t_rpcol on t (
    replace(string_col, 'b', 'AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA')
);
show indexes from t;

create index i_t_rpcol_1 on t (
    replace( cast( string_col as string collate utf8_en_ci),  'b', 'AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA'));
show indexes from t ;

create index i_comment on t (  string_col ) comment '';
show indexes from t ;


drop table if exists t;
